def unique_count(dataframe):
    for item in dataframe:
        print ('Count of', item, 'is', dataframe[item].nunique())

unique_count(df)

# print out # of missing values in each column. Need to decide which column need to remove missing values based on future analysis
# for instance, if I want to aggregate data by county, then 'County Name' must not be null
def missing_count(dataframe):
    for col in dataframe.columns:
        print('Count of missing', col, 'is', len(dataframe.loc[dataframe[col].isnull()]))

missing_count(df)
